#返回本季度
function  quarter_query($time){
    $month = date('m',$time);
    $quarter = floor(($month+2)/3);
    return $quarter;
}
#返回本季度第一天
function datetime_query_start($quarter,$date=false){
    $start = mktime(0,0,0,($quarter - 1) *3 +1,1,date('Y'));
    if($date){
        return date('Ymd',$start);
    }
    return $start;
}
#返回本季度最后一天
function datetime_query_end($quarter,$date=false){
    $end = mktime(0,0,0,$quarter * 3,1,date('Y'));
    if($date){
        return date('Ymd',$end);
    }
    return $end;
}
