/**
 * @param $arr
 * @param null $field  要排序的键
 * @param null $length 获取前多少条记录
 * @param $order  SORT_DESC|SORT_ASC
 * @return array|null
 */
function sort_field_arr($arr,$field=null,$length=null,$order){
    if(empty($field)){
        return null;
    }
    //获取要排序的一维数组
    $arr1 = array_column($arr,$field);
    //排序
    array_multisort($arr1,$order,$arr);
    //返回长度
    $arr2 = array_slice($arr,0,$length);
    return $arr2;
}
