   * 用户点击右上角分享
   */
  onShareAppMessage: function () {
    return {
      title: this.data.song.name,
      path: '/pages/music/music?currentIndex='+this.data.currentIndex,
      imageUrl: this.data.song.cover,
    }
  },
  parseLyric: function (lrc_s) {
    if (lrc_s) {
      lrc_s = lrc_s.replace(/([^\]^\n])\[/g, (match, p1) => p1 + '\n[');
      const lyric = lrc_s.split('\n');
      let lrc = [];
      const lyricLen = lyric.length;
      for (let i = 0; i < lyricLen; i++) {
        // match lrc time
        const lrcTimes = lyric[i].match(/\[(\d{2}):(\d{2})(\.(\d{2,3}))?]/g);
        // match lrc text
        const lrcText = lyric[i].replace(/.*\[(\d{2}):(\d{2})(\.(\d{2,3}))?]/g, '').replace(/<(\d{2}):(\d{2})(\.(\d{2,3}))?>/g, '').replace(/^\s+|\s+$/g, '');

        if (lrcTimes) {
          // handle multiple time tag
          const timeLen = lrcTimes.length;
          for (let j = 0; j < timeLen; j++) {
            const oneTime = /\[(\d{2}):(\d{2})(\.(\d{2,3}))?]/.exec(lrcTimes[j]);
            const min2sec = oneTime[1] * 60;
            const sec2sec = parseInt(oneTime[2]);
            const msec2sec = oneTime[4] ? parseInt(oneTime[4]) / ((oneTime[4] + '').length === 2 ? 100 : 1000) : 0;
            const lrcTime = min2sec + sec2sec + msec2sec;
            lrc.push([lrcTime, lrcText]);
          }
        }
      }
      // sort by time
      lrc = lrc.filter((item) => item[1]);
      lrc.sort((a, b) => a[0] - b[0]);
      return lrc;
    }
    else {
      return [];
    }
  },
  sliceNull: function (lrc) {
    var result = []
    for (var i = 0; i < lrc.length; i++) {
      if (lrc[i][1] == "") {
      } else {
        result.push(lrc[i]);
      }
    }
    return result
  },
  timeToString: function (duration) {
    let str = '';
    let minute = parseInt(duration / 60) < 10 ? ('0' + parseInt(duration / 60)) : (parseInt(duration / 60));
    let second = duration % 60 < 10 ? ('0' + duration % 60) : (duration % 60);
    str = minute + ':' + second;
    return str;
  },
  onInit: function(index){
    let that = this
    //设置当前播放
    let song = this.data.datalist[index]
    //获取歌词
    wx.request({
      url: song.lrc,
      success: (res) => {
        this.setData({
          currentIndex: index,
          song: song,
          lry: that.sliceNull(that.parseLyric(res.data))
        })
        let lry = this.data.lry

        backgroundAudioManager.src = this.data.song.url
        backgroundAudioManager.title = this.data.song.name
        backgroundAudioManager.singer = this.data.song.artist
        backgroundAudioManager.coverImgUrl = this.data.song.cover
        backgroundAudioManager.webUrl = '/pages/music/music?currentIndex='+this.data.currentIndex

        backgroundAudioManager.onTimeUpdate(function () {
          let currentTime = backgroundAudioManager.currentTime
          let duration = backgroundAudioManager.duration
          let index = that.data.currentLry
          that.setData({
            progress: currentTime / duration * 100,
            currentPosition: that.timeToString(parseInt(currentTime)),
            duration: that.timeToString(parseInt(duration))
          })
          if (index > lry.length - 1 || currentTime < lry[index][0] || (!lry[index + 1] || currentTime >= lry[index + 1][0])) {
            for (let i = 0; i < lry.length; i++) {
              if (currentTime >= lry[i][0] && (!lry[i + 1] || currentTime < lry[i + 1][0])) {
                that.setData({
                  currentLry: i,
                  marginTop: i * 16
                })
              }
            }
          }
          that.onEven()
        });
        that.onPlay()
      }
    })
  },
  selectSong: function(e){
    let index = e.currentTarget.dataset.index
    if(index==this.data.currentIndex) return;
    this.onInit(index)
    this.setData({
      isPlaying: false
    })
  },
  onPlay: function(){
    backgroundAudioManager.play()
    this.setData({
      isPlaying: true
    })
  },
  onPause: function(){
    backgroundAudioManager.pause()
    this.setData({
      isPlaying: false
    })
  },
  //监听播放事件
  onEven: function(){
    let that = this
    backgroundAudioManager.onStop(function () {
      that.setData({
        isPlaying: false
      })
    })
    backgroundAudioManager.onPlay(function () {
      that.setData({
        isPlaying: true
      })
    })
    backgroundAudioManager.onPause(function () {
      that.onPause()
    })
    backgroundAudioManager.onEnded(function () {
      let length = that.data.datalist.length
      let index = that.data.currentIndex + 1
      if (index > length) {
        backgroundAudioManager.stop()
        this.setData({
          isPlaying: false
        })
      } else {
        that.onInit(index)
      }
    })
    backgroundAudioManager.onNext(function(){
      let length = that.data.datalist.length
      let index = that.data.currentIndex + 1
      if(index > length){
        return
      }else{
        that.onInit(index)
      }
    })
    backgroundAudioManager.onPrev(function () {
      let index = that.data.currentIndex - 1
      if (index < 0) {
        return
      } else {
        that.onInit(index)
      }
    })
  }
})
