      </view>
    </view>
    <view class="aplayer-info">
      <view class="aplayer-music">
        <text class="aplayer-title">{{song.name}}</text>
        <text class="aplayer-author"> - {{song.artist}}</text>
      </view>
      <view class="aplayer-lrc">
        <scroll-view class="aplayer-lrc-contents" scroll-y="true" scroll-top="{{marginTop}}">
          <view wx:for="{{lry}}" wx:key="index" class="{{currentLry==index?'aplayer-lrc-current':''}}">{{item[1]}}</view>
        </scroll-view>
      </view>
      <view class="aplayer-controller">
        <view class="aplayer-bar-wrap">
          <progress  class="aplayer-bar" stroke-width="2" activeColor="#cdcdcd" percent="{{progress}}" />
        </view>
        <view class="aplayer-time">
          <view class="aplayer-time-inner">
            <text>{{currentPosition}}</text>
            /
            <text>{{duration}}</text>
            <view class="aplayer-icon" bindtap='onShareAppMessage'>❤</view>
          </view>       
        </view>
      </view>
    </view>
  </view>
  <view class="aplayer-list">
    <view wx:for="{{datalist}}" wx:key="index" bindtap='selectSong' data-index='{{index}}'>
      <text class="aplayer-list-cur" style="background-color: #ad7a86;{{currentIndex==index?'display:block':'display:none'}}"></text>
      <text class="aplayer-list-index">{{index+1}}</text>
      <text class="aplayer-list-title">{{item.name}}</text>
      <text class="aplayer-list-author">{{item.artist}}</text>
    </view>
  </view>
</view>
